select id,`title`,`author`,`short`,`cover`,`summary`,`tags`,`content`,`count`,`update_time`
from
(select articles.id,`title`,`author`,`short`,`cover`,`summary`,`tags`,`content`,`count`,`update_time`,
match(`title_index`)
against("?")
as score
from articles inner join articles_index on articles.id=articles_index.id
where match(`title_index`)
against("?"))AS T
union
select id,`title`,`author`,`short`,`cover`,`summary`,`tags`,`content`,`count`,`update_time`
from
(select articles.id,`title`,`author`,`short`,`cover`,`summary`,`tags`,`content`,`count`,`update_time`,
match(`short_index`, `content_index`, `tag_index`)
against("?")
as score
from articles inner join articles_index on articles.id=articles_index.id
where match(`short_index`, `content_index`, `tag_index`)
against("?"))AS T;
